"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { useAuth } from "@/context/AuthContext";
import { db } from "@/lib/firebase";
import { doc, setDoc, serverTimestamp } from "firebase/firestore";

// ----------------------------------------------------------------------
// 1. PASTE YOUR BASE64 STRINGS HERE
// ----------------------------------------------------------------------
const MOBILE_BLUR_DATA_URL =
  "data:image/webp;base64,";
const DESKTOP_BLUR_DATA_URL =
  "data:image/webp;base64,";

export default function WaitlistPage() {
  const [email, setEmail] = useState("");
  const [status, setStatus] = useState("idle");
  const [message, setMessage] = useState("");

  const { user, loading } = useAuth();
  const router = useRouter();

  useEffect(() => {
    if (!loading && user && user.usernameTag) {
      router.push(`/${user.usernameTag}`);
    }
  }, [user, loading, router]);

  // FIX: Removed ": React.FormEvent" so this is now valid JavaScript
  const handleSubmit = async (e) => {
    e.preventDefault();
    const cleanEmail = email.trim().toLowerCase();

    if (!cleanEmail || !cleanEmail.includes("@")) {
      setStatus("error");
      setMessage("Please enter a valid email address.");
      return;
    }

    setStatus("loading");
    setMessage("");

    try {
      await setDoc(doc(db, "waitlist", cleanEmail), {
        email: cleanEmail,
        createdAt: serverTimestamp(),
      });
      setStatus("success");
      setEmail("");
    } catch (error) {
      console.error(error);
      setStatus("error");
      setMessage("Something went wrong. Please try again later.");
    }
  };

  return (
    <div className="relative min-h-screen overflow-hidden font-sans text-white bg-black">
      {/* ----------------------------------
         BACKGROUND SYSTEM
      ---------------------------------- */}
      <div className="absolute inset-0 z-0">
        {/* 
           LAYER 1: INSTANT PLACEHOLDERS (Base64)
        */}

        {/* Mobile Placeholder (< 768px) */}
        <div
          className="absolute inset-0 bg-cover bg-center md:hidden"
          style={{ backgroundImage: `url(${MOBILE_BLUR_DATA_URL})` }}
          aria-hidden="true"
        />

        {/* Desktop Placeholder (>= 768px) */}
        <div
          className="absolute inset-0 bg-cover bg-center hidden md:block"
          style={{ backgroundImage: `url(${DESKTOP_BLUR_DATA_URL})` }}
          aria-hidden="true"
        />

        {/* 
           LAYER 2: HIGH RES IMAGE
        */}
        <picture>
          <source srcSet="/background-800.webp" media="(max-width: 768px)" />

          <img
            src="/background-1920.webp"
            alt="Background"
            fetchPriority="high"
            decoding="async"
            className="relative h-full w-full object-cover opacity-0 motion-safe:animate-[fadeInBlur_1.5s_cubic-bezier(0.16,1,0.3,1)_forwards]"
          />
        </picture>

        {/* LAYER 3: DARKENING OVERLAY */}
        <div className="absolute inset-0 bg-black/30 pointer-events-none" />
      </div>

      {/* ----------------------------------
         CONTENT
      ---------------------------------- */}
      <div className="relative z-10 flex min-h-screen flex-col items-center justify-start pt-[19svh] md:justify-center md:pt-0 px-4">
        <div className="w-full max-w-lg">
          <div className="relative overflow-hidden rounded-md bg-black/60 px-8 py-10 shadow-2xl backdrop-blur-[1px] border border-white/5 sm:px-14">
            <div className="text-center">
              <h1 className="flex flex-wrap items-center justify-center gap-1">
                <span className="text-3xl md:text-4xl font-semibold text-white">
                  volvox.pics
                </span>
                <span className="text-2xl md:text-3xl text-gray-400 whitespace-nowrap">
                  /your-profile
                </span>
              </h1>
              <p className="mt-5 mb-3 text-zinc-300">
                Collect and share your photo albums
              </p>
            </div>

            <form className="space-y-5" onSubmit={handleSubmit}>
              <input
                type="email"
                placeholder="Email address"
                value={email}
                disabled={status === "loading" || status === "success"}
                onChange={(e) => setEmail(e.target.value)}
                className="block w-full rounded-sm bg-zinc-900/50 px-4 py-3 text-white placeholder:text-zinc-600 ring-1 ring-white/10 focus:ring-white/40 outline-none disabled:opacity-50"
              />

              <button
                type="submit"
                disabled={status !== "idle" && status !== "error"}
                className={`
                  w-full rounded-sm py-3 text-sm font-semibold
                  transition-all
                  ${
                    status === "success"
                      ? "bg-zinc-800 text-green-500 cursor-default"
                      : "bg-zinc-300 text-neutral-800 hover:bg-zinc-400"
                  }
                `}
              >
                {status === "loading"
                  ? "Joiningâ€¦"
                  : status === "success"
                  ? "You're on the list"
                  : "Join Waitlist"}
              </button>

              {message && (
                <div
                  className={`text-center text-xs uppercase tracking-wide ${
                    status === "error" ? "text-red-500" : "text-zinc-400"
                  }`}
                >
                  {message}
                </div>
              )}
            </form>
          </div>
        </div>
      </div>

      {/* LOGIN BUTTON */}
      <div className="fixed top-6 right-6 z-20">
        <a
          href="/login"
          className="flex items-center gap-2 rounded-sm bg-black/30 px-4 py-2 text-sm text-zinc-300 backdrop-blur-[1px] border border-white/10 hover:bg-black/60 hover:text-white transition-colors"
        >
          Login
        </a>
      </div>
    </div>
  );
}
